import React, {useState} from "react";

const MadlibForm = () => {
    const [formData, setFormData] = useState({
        nounOne: "",
        nounTwo: "",
        adjective: "",
        color: ""
    });

    const handleChange = e => {
        const { name, value } = e.target;
        setFormData(data => ({
            ...data,
            [name]: value
        }));
    };
    return (
        <form onSubmit={handleChange}>
            <input 
            type="text"
            placeholder="noun"
            name="nounOne"
            />
            <input 
            type="text"
            placeholder="noun 2"
            name="nounTwo"
            />
            <input 
            type="text"
            placeholder="adjective"
            name="adjective"
            />
            <input 
            type="text"
            placeholder="color"
            name="color"
            />
            <button>Get Story</button>
        </form>
    )
}

export default MadlibForm
